Scripts.OnClick:
if TRP_Module_PlayerInfo[Royaume][Joueur]["Sauvegarde"]["1"]["Prenom"] then
	SavePersonnage:Hide();
	StaticPopupDialogs["TRP_ECRASE_SLOT"].text = setTRPColorToString(TRP_ENTETE.." \n "..ENREGISTRER.." :\n\n"..ERASEPERSO);
	TRP_ShowStaticPopup("TRP_ECRASE_SLOT",nil,nil,"1");
else
	TRP_ShowStaticPopup("TRP_SAVE_PERSO",nil,nil,"1");
end

if TRP_Module_PlayerInfo[Royaume][Joueur]["Sauvegarde"]["2"]["Prenom"] then
