Scripts.OnLoad:
ListeIconesSliderText:SetText("");
ListeIconesSliderHigh:SetText("");
ListeIconesSliderLow:SetText("");
this:SetMinMaxValues(0, 6);
this:SetValueStep(1);
this:SetOrientation("VERTICAL");

ListeObjetsSliderText:SetText("");
ListeObjetsSliderHigh:SetText("");
ListeObjetsSliderLow:SetText("");
this:SetMinMaxValues(0, 6);
this:SetValueStep(1);
this:SetOrientation("VERTICAL");

ListeDocumentsSliderText:SetText("");
ListeDocumentsSliderHigh:SetText("");
ListeDocumentsSliderLow:SetText("");
this:SetMinMaxValues(0, 6);
this:SetValueStep(1);
this:SetOrientation("VERTICAL");

Scripts.OnTextChanged:
if tonumber(this:GetText()) ~= nil and tonumber(this:GetText()) > tonumber(SendMailBoxMax:GetText()) then
	this:SetText(SendMailBoxMax:GetText());
end

Scripts.OnLoad:
ListePersonnagesSliderText:SetText("");
ListePersonnagesSliderHigh:SetText("");
ListePersonnagesSliderLow:SetText("");
this:SetMinMaxValues(0, 6);
this:SetValueStep(1);
this:SetOrientation("VERTICAL");

Scripts.OnTextChanged:
if tonumber(this:GetText()) ~= nil and tonumber(this:GetText()) > tonumber(SendPersoBoxMax:GetText()) then
	this:SetText(SendPersoBoxMax:GetText());
end

Scripts.OnLoad:
ListeSoundSliderText:SetText("");
ListeSoundSliderHigh:SetText("");
ListeSoundSliderLow:SetText("");
this:SetMinMaxValues(0, 6);
this:SetValueStep(1);
this:SetOrientation("VERTICAL");

Scripts.OnTextChanged:
local max = 240-string.len(TRPModifyPetInfo.creatureName);
local total = string.len(this:GetText()) + string.len(TRPModifyPetInfoDescriSaisie:GetText())
if total > max then
	this:SetText(string.sub(this:GetText(),0,string.len(this:GetText())-(total-max)));
end

local max = 240-string.len(TRPModifyPetInfo.creatureName);
local total = string.len(this:GetText()) + string.len(TRPModifyPetInfoNomSaisie:GetText())
if total > max then
	this:SetText(string.sub(this:GetText(),0,string.len(this:GetText())-(total-max)));
end
