Scripts.OnClick:
if button == "LeftButton" then
	if IsControlKeyDown() and TRP_Module_ObjetsPerso[string.gsub(this:GetName(),"ObjetPersoSlot","")] ~= nil then
		DonnerReference(string.gsub(this:GetName(),"ObjetPersoSlot",""));
	else
		EditObjetPerso(string.gsub(this:GetName(),"ObjetPersoSlot",""));
	end
elseif button == "MiddleButton" then
	if TRP_Module_ObjetsPerso[string.gsub(this:GetName(),"ObjetPersoSlot","")] ~= nil then
		GetObjets(TRP_Module_ObjetsPerso[string.gsub(this:GetName(),"ObjetPersoSlot","")]["ID"],-1,1);
	end
else
	if TRP_Module_ObjetsPerso[string.gsub(this:GetName(),"ObjetPersoSlot","")] ~= nil then
		StaticPopupDialogs["TRP_INV_DELETE_REF"].text = setTRPColorToString(TRP_ENTETE.." \n "..DELETEREF.." :\n\n"..CONFIRMSUPPREF);
		TRP_ShowStaticPopup("TRP_INV_DELETE_REF",nil,nil,string.gsub(this:GetName(),"ObjetPersoSlot",""));
	end
end

