Scripts.OnValueChanged:
if this:GetValue() > 0 then
	PanelObjetSliderCategorieText:SetText(TRP_ObjetCategorie[this:GetValue()].Nom);
	PanelObjetSliderSousCategorie:Show();
	PanelObjetSliderSousCategorie:SetMinMaxValues(0, #TRP_ObjetCategorie[this:GetValue()].SousCategorie);
	PanelObjetSliderSousCategorie:SetValue(0);
else
	PanelObjetSliderSousCategorie:SetValue(0);
	PanelObjetSliderCategorieText:SetText("All");
	PanelObjetSliderSousCategorie:Hide();
end
if this:IsVisible() then
	if PanelCoffreSacFrameType:GetText() == COFFREFINANCES then
		InventaireOpen("InventaireOngletSacADos");
	elseif PanelCoffreSacFrameType:GetText() == COFFREVOYAGE then
		if IsMounted() then
			InventaireOpen("InventaireOngletCoffre");
		else
			TRPError("Vous \195\170tes descendu votre monture.");
			InventaireOpen("InventaireOngletSacADos");
		end
	elseif PanelCoffreSacFrameType:GetText() == PLANQUE then
		if DetectPlanque() ~= nil then
			InventaireOpen("InventaireOngletPlanques");
		else
			TRPError("Vous vous \195\170tes \195\169loign\195\169 de votre planque.");
			InventaireOpen("InventaireOngletSacADos");
		end
	end
end

if this:GetValue() > 0 and this:IsVisible() then
	PanelObjetSliderSousCategorieText:SetText(TRP_ObjetCategorie[PanelObjetSliderCategorie:GetValue()].SousCategorie[this:GetValue()]);
else
	PanelObjetSliderSousCategorieText:SetText("Toutes");
end
if this:IsVisible() then
	if PanelCoffreSacFrameType:GetText() == COFFREFINANCES then
		InventaireOpen("InventaireOngletSacADos");
	elseif PanelCoffreSacFrameType:GetText() == COFFREVOYAGE then
		if IsMounted() then
			InventaireOpen("InventaireOngletCoffre");
		else
			TRPError("Vous \195\170tes descendu votre monture.");
			InventaireOpen("InventaireOngletSacADos");
		end
	elseif PanelCoffreSacFrameType:GetText() == PLANQUE then
		if DetectPlanque() ~= nil then
			InventaireOpen("InventaireOngletPlanques");
		else
			TRPError("Vous vous \195\170tes \195\169loign\195\169 de votre planque.");
			InventaireOpen("InventaireOngletSacADos");
		end
	end
end

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_TOPLEFT");
GameTooltip:AddLine("New Object",1,1,1);
GameTooltip:AddLine("Left-click: Create a new object",0,1,0);
GameTooltip:AddLine("Right-click Create from ...",0,1,0);
GameTooltip:Show()

Scripts.OnClick:
if arg1 == "RightButton" then
	OpenListeObjets("PanelObjetPersoFromHidden");							
else
	createEmptyObjet();
end

Scripts.OnValueChanged:
if this:GetValue() > 0 then
	PanelObjetPersoSliderCategorieText:SetText("Category : "..TRP_ObjetCategorie[this:GetValue()].Nom);
	PanelObjetPersoSliderSousCategorie:Show();
	PanelObjetPersoSliderSousCategorie:SetMinMaxValues(0, #TRP_ObjetCategorie[this:GetValue()].SousCategorie);
	PanelObjetPersoSliderSousCategorie:SetValue(0);
else
	PanelObjetPersoSliderCategorieText:SetText("Category : ".."Toutes");
	PanelObjetPersoSliderSousCategorie:Hide();
end
if this:IsVisible() then
	ShowObjetPerso();
end

if this:GetValue() > 0 and this:IsVisible() then
	PanelObjetPersoSliderSousCategorieText:SetText("Subcategory : "..TRP_ObjetCategorie[PanelObjetPersoSliderCategorie:GetValue()].SousCategorie[this:GetValue()]);
else
	PanelObjetPersoSliderSousCategorieText:SetText("Subcategory : ".."Toutes");
end
if this:IsVisible() then
	ShowObjetPerso();
end
