Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_TOPLEFT");
GameTooltip:AddLine(COFFREVOYAGE, 1, 1, 1);
if not IsMounted() then
	GameTooltip:AddLine(ObjectError["MUSTBOONMOUNT"], 1, 0, 0);
end
GameTooltip:Show();

GameTooltip:SetOwner(this, "ANCHOR_TOPLEFT");
GameTooltip:AddLine(PLANQUES, 1, 1, 1);
if DetectPlanque() then
	GameTooltip:AddLine("|cff00ff00"..PLANQUEACCES, 1, 1, 1);
else
	GameTooltip:AddLine("|cff00ff00"..CREERPLANQUETOOL, 1, 1, 1);
end
GameTooltip:Show();

Scripts.OnLoad:
DocumentsSlot1Delete:SetScript("OnClick", 
	function(self,button,down)
		deleteDocuments(DocumentsSlot1ID:GetText());
end );
DocumentsSlot1Edit:SetScript("OnClick", 
	function(self,button,down)
		if TRP_Module_Documents[DocumentsSlot1ID:GetText()]["Lock"] and TRP_Module_Documents[DocumentsSlot1ID:GetText()]["Createur"] ~= Joueur then
			TRPError("The document author have locked the document, you can't edit it.");
		else
			PanelOpen("FicheJoueurOngletDocument","DocumentsPanelModifier",DocumentsSlot1ID:GetText());
		end
end );
DocumentsSlot1Edit:SetScript("OnEnter", 
	function()
		GameTooltip:SetOwner(this, "ANCHOR_TOPLEFT");
		if TRP_Module_Documents[DocumentsSlot1ID:GetText()]["Lock"] then
			if TRP_Module_Documents[DocumentsSlot1ID:GetText()]["Createur"] ~= Joueur then
				GameTooltip:AddLine("Document Locked",1,0,0);
				GameTooltip:AddLine("Document locked by the author,|cffffffff "..TRP_Module_Documents[DocumentsSlot1ID:GetText()]["Createur"],1,0.5,0);
			else
				GameTooltip:AddLine("Edit",1,1,1);
				GameTooltip:AddLine("You have locked this document",1,0.75,0);
			end
		else
			GameTooltip:AddLine("Edit",1,1,1);
		end
		GameTooltip:Show();
end );

Scripts.OnEnter:
Reinit_DocumentsEdit();
getglobal("DocumentsSlot1Edit"):Show();
getglobal("DocumentsSlot1Delete"):Show();
GameTooltip:SetOwner(this, "ANCHOR_TOPLEFT");
if TRP_Module_Documents[DocumentsSlot1ID:GetText()]["Createur"] == Joueur then
	GameTooltip:AddLine(DocumentsTexts["YOUCREATEUR"],0,1,0);
	GameTooltip:AddLine("Consult the document",1,1,1);
else
	GameTooltip:AddLine(DocumentsTexts["CREATEUR"]..TRP_Module_Documents[DocumentsSlot1ID:GetText()]["Createur"],1,0.75,0);
end
if UnitName("target") ~= nil and UnitName("target") ~= Joueur and UnitIsPlayer("target") and UnitFactionGroup("target") == UnitFactionGroup("player") and 
CheckInteractDistance("target",3) ~= nil then
	GameTooltip:AddLine(CLICGAUCHECTRL.." : Give the document to "..UnitName("target"),0.5,0.5,1);
end
GameTooltip:Show()

