Scripts.OnClick:
if arg1 == "RightButton" then
	OpenListeDocuments("PanelDocumentsFromHidden");							
else
	createEmptyDocument();
end

Scripts.OnTabPressed:
if IsShiftKeyDown() then
	DocumentCreateTexteScrollTexte:SetFocus();
else
	DocumentCreateAuteurVignette:SetFocus();
end

if IsShiftKeyDown() then
	DocumentCreateTitreVignette:SetFocus();
else
	DocumentCreateDateVignette:SetFocus();
end

if IsShiftKeyDown() then
	DocumentCreateAuteurVignette:SetFocus();
else
	DocumentCreateIconVignette:SetFocus();
end

if IsShiftKeyDown() then
	DocumentCreateDateVignette:SetFocus();
else
	DocumentCreateTexteScrollTexte:SetFocus();
end

Scripts.OnTextChanged:
if string.find(this:GetText(),"%D") then
	this:SetText(string.gsub(this:GetText(),"|",""));
end
if string.len(this:GetText()) > 3500 then
	this:SetText(string.sub(this:GetText(),0,3500));
end
DocumentCreateTexteScrollCount:SetText("( "..string.len(this:GetText()).." / 3500 )");
refreshApercu();

Scripts.OnTabPressed:
if IsShiftKeyDown() then
	DocumentCreateIconVignette:SetFocus();
else
	DocumentCreateTitreVignette:SetFocus();
end
