Scripts.OnClick:
if arg1 == "LeftButton" then
	if MenuPrincipal:IsVisible() then
		OpenMainMenu(false);
	else
		OpenMainMenu(true);
	end
elseif arg1 == "RightButton" then
	if TRPActionsBar:IsVisible() then
		OpenActionsBar(false);
	else
		OpenActionsBar(true);
	end
end

Scripts.OnEnter:
GameTooltip:SetOwner(MinimapButton, "ANCHOR_LEFT");
GameTooltip:AddLine(TOTALRP, 1, 1, 1);
GameTooltip:AddLine("Left click: Open menu",1,0.75,0);
GameTooltip:AddLine("Right click: Show shortcuts bar",1,0.75,0);
local count = 0;
table.foreach(TRP_Module_Inventaire[Royaume][Joueur]["Sacs"]["InBox"], function()
	count = count + 1;
end);
if count > 0 then
	GameTooltip:AddLine(" ",1,0.75,0);
	GameTooltip:AddLine("You have ".. count .." packages awaiting to be read. \ nGo to a mailbox!",0.25,1,0);
end
count = 0;
table.foreach(TRP_Module_Inventaire[Royaume][Joueur]["Sacs"]["OutBox"], function()
	count = count + 1;
end);
if count > 0 then
	GameTooltip:AddLine(" ",1,0.75,0);
	GameTooltip:AddLine("You have ".. count .." packages awaiting to be sent. \ nGo to a mailbox!",0.25,1,0);
end
GameTooltip:Show();

