Scripts.OnClick:
if MainInventaireFrame:IsVisible() and FicheJoueurPanelTitle:GetText() == INVENTAIRESACADOS then
	CloseAll();
	OpenMainMenu(true);
else
	PanelOpen("FicheJoueurOngletInventaire","InventaireOngletSacADos")
end

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_TOPLEFT");
GameTooltip:AddLine("Open your backpack", 1, 1, 1);
local Num = 0;
local Qte = 0;
table.foreach(TRP_Module_Inventaire[Royaume][Joueur],
	function(objet)
		if TRP_Module_Inventaire[Royaume][Joueur][objet]["Sac"] == "-1" then
			Num = Num + 1;
			Qte = Qte + TRP_Module_Inventaire[Royaume][Joueur][objet]["Qte"];
		end
end);
if Num > 0 then
	GameTooltip:AddLine(" ", 1, 1, 1);
	GameTooltip:AddLine(Num.." item types in total.", 0, 1, 0);
	GameTooltip:AddLine(Qte.." items in total.", 0, 1, 0);
end
GameTooltip:Show();

Scripts.OnClick:
if IsMounted() then
	if MainInventaireFrame:IsVisible() and FicheJoueurPanelTitle:GetText() == INVENTAIRECOFFRE then
		CloseAll();
		OpenMainMenu(true);
	else
		PanelOpen("FicheJoueurOngletInventaire","InventaireOngletCoffre")
	end
else
	TRPError(ObjectError["NOMOUNT"]);
end

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_TOPLEFT");
GameTooltip:AddLine("Open your safe", 1, 1, 1);
if not IsMounted() then
	GameTooltip:AddLine(ObjectError["MUSTBOONMOUNT"], 1, 0, 0);
end
local Num = 0;
local Qte = 0;
table.foreach(TRP_Module_Inventaire[Royaume][Joueur],
	function(objet)
		if TRP_Module_Inventaire[Royaume][Joueur][objet]["Sac"] == "0" then
			Num = Num + 1;
			Qte = Qte + TRP_Module_Inventaire[Royaume][Joueur][objet]["Qte"];
		end
end);
if Num > 0 then
	GameTooltip:AddLine(" ", 1, 1, 1);
	GameTooltip:AddLine(Num.." item types in total.", 0, 1, 0);
	GameTooltip:AddLine(Qte.." items in total.", 0, 1, 0);
end
GameTooltip:Show();

Scripts.OnUpdate:
this.TimeSinceLastUpdate = this.TimeSinceLastUpdate + arg1; 	
if (this.TimeSinceLastUpdate > 0.2) then
	if WorldMapFrame:IsVisible() then
		BarreActionsPlanquesIcon:SetDesaturated(true);
		BarreActionsPlanques:Disable();
	else
		BarreActionsPlanquesIcon:SetDesaturated(false);
		BarreActionsPlanques:Enable();
	end
	if DetectPlanque() then
		if IconeZone[GetCurrentMapContinent()] and IconeZone[GetCurrentMapContinent()][GetCurrentMapZone()] then
			BarreActionsPlanquesIcon:SetTexture(IconeZone[GetCurrentMapContinent()][GetCurrentMapZone()]);
		end
	else
		BarreActionsPlanquesIcon:SetTexture("Interface\\Icons\\INV_Misc_Map07.blp");
	end
end

Scripts.OnClick:
if DetectPlanque() then
	if MainInventaireFrame:IsVisible() and FicheJoueurPanelTitle:GetText() == INVENTAIREPLANQUES then
		CloseAll();
		OpenMainMenu(true);
	else
		PanelOpen("FicheJoueurOngletInventaire","InventaireOngletPlanques");
	end
else
	if IconeZone[GetCurrentMapContinent()] == nil or IconeZone[GetCurrentMapContinent()][GetCurrentMapZone()] == nil then
		TRPError("You cannot create a hiding place here.");
	else
		TRP_ShowStaticPopup("TRP_CREATE_PLANQUE");
	end
end

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_TOPLEFT");
if DetectPlanque() then
	GameTooltip:AddLine("|cff00ff00"..PLANQUEACCES, 1, 1, 1);
	local Num = 0;
	local Qte = 0;
	table.foreach(TRP_Module_Inventaire[Royaume][Joueur],
		function(objet)
			if TRP_Module_Inventaire[Royaume][Joueur][objet]["Sac"] == generatePlanqueID() then
				Num = Num + 1;
				Qte = Qte + TRP_Module_Inventaire[Royaume][Joueur][objet]["Qte"];
			end
	end);
	if Num > 0 then
		GameTooltip:AddLine(" ", 1, 1, 1);
		GameTooltip:AddLine(Num.." item types in total.", 0, 1, 0);
		GameTooltip:AddLine(Qte.." items in total.", 0, 1, 0);
	end
else
	GameTooltip:AddLine("|cff00ff00"..CREERPLANQUETOOL, 1, 1, 1);
end
GameTooltip:Show();

