Scripts.OnUpdate:
if TRP_Module_Configuration["Modules"]["Sons"]["Activate"] then
	this:SetAlpha(1);
else
	this:SetAlpha(0.5);
end

Scripts.OnClick:
if IsShiftKeyDown() and TRP_Module_Configuration["Modules"]["Sons"]["Activate"] then
	TRP_ShowStaticPopup("TRP_SAISIESOUND");
elseif IsControlKeyDown() then
	if TRP_Module_Configuration["Modules"]["Sons"]["Activate"] then
		TRP_Module_Configuration["Modules"]["Sons"]["Activate"] = false;
	else
		TRP_Module_Configuration["Modules"]["Sons"]["Activate"] = true;
	end
else
	if TRPListePlaySound:IsVisible() then
		TRPListePlaySound:Hide();
	elseif TRP_Module_Configuration["Modules"]["Sons"]["Activate"] then
		OpenListePlaySound();
	end
end

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_TOPLEFT");
if UnitName("target") and not UnitIsPlayer("target") then
	GameTooltip:AddLine(UnitName("target"),0,1,0);
	GameTooltip:AddLine("Left-click: Make "..UnitName("target").." speak.",1,0.75,0);
	GameTooltip:AddLine("Right Click: Make "..UnitName("target").." do an emote.",1,0.75,0);
else
	GameTooltip:AddLine("Click: Make a non-targeted emote.",1,0.75,0);
	GameTooltip:AddLine("Shift + Left Click: Make someone speak (choice of name).",1,0.75,0);
	GameTooltip:AddLine("Shift + Right Click: Make someone do an emote (choice of name).",1,0.75,0);
end
GameTooltip:Show();

Scripts.OnClick:
if UnitName("target") and not UnitIsPlayer("target") then
	if arg1 == "LeftButton" then
		StaticPopupDialogs["TRP_EMOTENPCDIRE"].text = TRP_ENTETE.."Enter the dialogue that "..UnitName("target").." will tell.\nThis is a spoken dialogue (/say).";
		TRP_ShowStaticPopup("TRP_EMOTENPCDIRE",nil,nil,UnitName("target"));
	else
		StaticPopupDialogs["TRP_EMOTENPC"].text = TRP_ENTETE.."Enter the emote that "..UnitName("target").." will play.\nIs a emote /e.";
		TRP_ShowStaticPopup("TRP_EMOTENPC",nil,nil,UnitName("target"));
	end
else
	if IsShiftKeyDown() then
		if arg1 == "LeftButton" then
			TRP_ShowStaticPopup("TRP_EMOTENAMEDIRE");
		else
			TRP_ShowStaticPopup("TRP_EMOTENAME");
		end
	else
		TRP_ShowStaticPopup("TRP_EMOTEGLOBAL");
	end
end

Scripts.OnLoad:
ListePlaySoundSliderText:SetText("");
ListePlaySoundSliderHigh:SetText("");
ListePlaySoundSliderLow:SetText("");
this:SetMinMaxValues(0, 6);
this:SetValueStep(1);
this:SetOrientation("VERTICAL");

Scripts.OnEscapePressed:
this:ClearFocus();
if ChatFrameEditBox:IsVisible() then
	ChatFrameEditBox:SetFocus();
end

Scripts.OnUpdate:
local rot = this:GetFacing();
rot = rot + arg1*1;
if rot > math.pi*2 then
	rot = 0;
end
this:SetFacing(rot);

