Scripts.OnValueChanged:
if this:IsVisible() then
	ChargerListe(this:GetValue());
end
ListeButtonSlider:SetValue(0);

if ListeTypeSlider:IsVisible() then
	ChargerListe(ListeTypeSlider:GetValue());
end
ListeButtonSlider:SetValue(0);

