Scripts.OnValueChanged:
ChangeHumeur(this:GetValue());
if UnitName("target") == Joueur then
	changeTarget();
end

Scripts.OnTabPressed:
if IsShiftKeyDown() then
	FicheJoueurOrigineSaisie:SetFocus();
else
	FicheJoueurPrenomSaisie:SetFocus();
end

if IsShiftKeyDown() then
	FicheJoueurNomSaisie:SetFocus();
else
	FicheJoueurTitreSaisie:SetFocus();
end

if IsShiftKeyDown() then
	FicheJoueurPrenomSaisie:SetFocus();
else
	FicheJoueurSousTitreSaisie:SetFocus();
end

if IsShiftKeyDown() then
	FicheJoueurTitreSaisie:SetFocus();
else
	FicheJoueurAgeSaisie:SetFocus();
end

if IsShiftKeyDown() then
	FicheJoueurSousTitreSaisie:SetFocus();
else
	FicheJoueurOrigineSaisie:SetFocus();
end

if IsShiftKeyDown() then
	FicheJoueurAgeSaisie:SetFocus();
else
	FicheJoueurNomSaisie:SetFocus();
end

if IsShiftKeyDown() then
	DescriPhysiqueSaisieEdit:SetFocus();
else
	DescriPhysiqueSaisieEdit:SetFocus();
end

if IsShiftKeyDown() then
	DescriActuelleSaisieEdit:SetFocus();
else
	DescriActuelleSaisieEdit:SetFocus();
end

Scripts.OnValueChanged:
if this:IsVisible() then
	GameTooltip:SetOwner(this, "ANCHOR_BOTTOMLEFT");
	GameTooltip:AddLine(TEXTE_MORALE[ceil(this:GetValue()/333)],getglobal(this:GetName().."Text"):GetTextColor())
	decouperForTooltip(DETAIL_MORALE[ceil(this:GetValue()/333)],30,getglobal(this:GetName().."Text"):GetTextColor());
	GameTooltip:Show();
end

if this:IsVisible() then
	GameTooltip:SetOwner(this, "ANCHOR_BOTTOMLEFT");
	GameTooltip:AddLine(TEXTE_ETHIQUE[ceil(this:GetValue()/333)],getglobal(this:GetName().."Text"):GetTextColor())
	decouperForTooltip(DETAIL_ETHIQUE[ceil(this:GetValue()/333)],30,getglobal(this:GetName().."Text"):GetTextColor());
	GameTooltip:Show();
end

if this:IsVisible() then
	if this:GetValue() == 1 then
		TRPCadreResumeRSPStyleTexte:SetJustifyH("LEFT");
	elseif this:GetValue() == 2 then
		TRPCadreResumeRSPStyleTexte:SetJustifyH("CENTER");
	else
		TRPCadreResumeRSPStyleTexte:SetJustifyH("RIGHT");
	end
end
